import React from 'react';
import {ICrypt} from "../models/ICrypt";
import {cryptAPI} from "../services/CryptService";

const CryptList: React.FC = () => {
    const { data: cryptocurrencies, error, isLoading, refetch } = cryptAPI.useGetCryptocurrenciesQuery(10, {
        pollingInterval: 5000,
    });

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error fetching data</div>;

    return (
        <div>
            <table>
                <thead>
                <tr>
                    <th>Symbol</th>
                    <th>Logo</th>
                    <th>Price (USD)</th>
                    <th>Market Cap (USD)</th>
                    <th>Change (24h)%</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {cryptocurrencies?.data?.map((crypt: ICrypt) => (
                    <tr key={crypt.rank}>
                        <td>{crypt.symbol}</td>
                        <td>
                            <img height={32} width={32} src={`https://assets.coincap.io/assets/icons/${crypt.symbol.toLowerCase()}@2x.png`} alt={crypt.symbol}/>
                        </td>
                        <td>{parseFloat(crypt.priceUsd).toFixed(2)}</td>
                        <td>{parseFloat(crypt.marketCapUsd).toFixed(2)}</td>
                        <td>{parseFloat(crypt.changePercent24Hr).toFixed(2)}</td>
                        <td>
                            <button>Add</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default CryptList;
