import {createApi, fetchBaseQuery} from "@reduxjs/toolkit/dist/query/react";
import {ICrypt} from "../models/ICrypt";

export const cryptAPI = createApi({
    reducerPath: 'cryptAPI',
    baseQuery: fetchBaseQuery({baseUrl: 'api.coincap.io'}),
    tagTypes: ['Crypt'],
    endpoints: (build) => ({
        getCryptocurrencies: build.query<ICrypt[], number>({
            query: (limit: number = 5) => ({
                mode: 'no-cors',
                url: `/v1/cryptocurrency/listings/latest`,
                headers: {
                    'X-CMC_PRO_API_KEY': '773f69e3-d5f4-4cc9-b6e4-dc9dc61fd1aa',
                },
                params: {
                    limit: limit,
                },
            }),
            providesTags: result => ['Crypt']
        }),
    })
})
