import React from 'react';
import {ICrypt} from "../models/ICrypt";
import {cryptAPI} from "../services/CryptService";

const CryptList: React.FC = () => {
    const { data, error, isLoading } = cryptAPI.useGetCryptocurrenciesQuery(10);  // Например, получаем топ-10 криптовалют

    if (isLoading) return <div>Загрузка...</div>;
    if (!data) return null;

    return (
        <div>
            {data.map((crypto: ICrypt) => (
                <div key={crypto.rank}>
                    <h4>{crypto.name} ({crypto.symbol})</h4>
                    <p>Ранг: {crypto.rank}</p>
                    <p>Капитализация: ${crypto.marketCapUsd.toLocaleString()}</p>
                    <p>Цена: ${crypto.priceUsd.toLocaleString()}</p>
                    <p>Изменение за 24 часа: {crypto.changePercent24Hr.toFixed(2)}%</p>
                </div>
            ))}
        </div>
    );
}

export default CryptList;
