import React, { useEffect, useState } from 'react';
import { ICrypt } from "../models/ICrypt";
import { cryptAPI } from "../services/CryptService";

const ITEMS_PER_PAGE = 15;

const CryptList: React.FC = () => {
    const [hasMoreData, setHasMoreData] = useState(true);
    const [page, setPage] = useState(1);
    const [search, setSearch] = useState('');
    const [sortOrder, setSortOrder] = useState<'ascending' | 'descending' | 'none'>('none');
    const [sortBy, setSortBy] = useState<'price' | 'marketCap' | 'change' | 'none'>('none');

    useEffect(() => {
        setPage(1);
    }, [search]);

    const { data: cryptocurrencies, error, isLoading } = cryptAPI.useGetCryptocurrenciesQuery({
        limit: ITEMS_PER_PAGE,
        offset: (page - 1) * ITEMS_PER_PAGE,
        search: search
    });

    useEffect(() => {
        if (cryptocurrencies && cryptocurrencies?.data?.length < ITEMS_PER_PAGE) {
            setHasMoreData(false);
        } else {
            setHasMoreData(true);
        }
    }, [cryptocurrencies]);

    const toggleSortOrder = (column: 'price' | 'marketCap' | 'change') => {
        if (sortBy !== column) {
            setSortBy(column);
            setSortOrder('ascending');
        } else {
            setSortOrder(prev => prev === 'ascending' ? 'descending' : 'ascending');
        }
    };

    const sortFunction = (a: ICrypt, b: ICrypt) => {
        let valueA = 0;
        let valueB = 0;

        switch (sortBy) {
            case 'price':
                valueA = parseFloat(a.priceUsd || "0");
                valueB = parseFloat(b.priceUsd || "0");
                break;
            case 'marketCap':
                valueA = parseFloat(a.marketCapUsd || "0");
                valueB = parseFloat(b.marketCapUsd || "0");
                break;
            case 'change':
                valueA = parseFloat(a.changePercent24Hr || "0");
                valueB = parseFloat(b.changePercent24Hr || "0");
                break;
            default:
                return 0;
        }

        return sortOrder === 'ascending' ? valueA - valueB : valueB - valueA;
    };

    const formatValue = (value: string | null) => {
        if (value === null) return '-';
        const roundedToTwo = parseFloat(value).toFixed(2);
        if (roundedToTwo === "0.00") {
            return parseFloat(value).toFixed(8);
        }
        return roundedToTwo;
    }

    const isValueZeroOrNull = (val: string | null) => {
        if (val === null) return true;
        const floatVal = parseFloat(val);
        return floatVal === 0 || isNaN(floatVal);
    }

    const filteredCryptocurrencies = cryptocurrencies?.data?.filter((crypt: ICrypt) => {
        return !isValueZeroOrNull(crypt.priceUsd) &&
            !isValueZeroOrNull(crypt.marketCapUsd) &&
            !isValueZeroOrNull(crypt.changePercent24Hr);
    });

    const sortedCryptocurrencies = sortOrder !== 'none'
        ? [...(filteredCryptocurrencies || [])].sort(sortFunction)
        : filteredCryptocurrencies;

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error fetching data</div>;

    const handleImageError = (e: React.SyntheticEvent<HTMLImageElement, Event>) => {
        e.currentTarget.src = "https://coincap.io/static/logo_mark.png";
    }

    return (
        <div>
            <input
                type="text"
                placeholder="Search..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
            />

            <table>
                <thead>
                <tr>
                    <th>Symbol</th>
                    <th>Logo</th>
                    <th onClick={() => toggleSortOrder('price')}>
                        Price (USD)
                        {sortBy === 'price' && (sortOrder === 'ascending' ? ' ↑' : ' ↓')}
                    </th>
                    <th onClick={() => toggleSortOrder('marketCap')}>
                        Market Cap (USD)
                        {sortBy === 'marketCap' && (sortOrder === 'ascending' ? ' ↑' : ' ↓')}
                    </th>
                    <th onClick={() => toggleSortOrder('change')}>
                        Change (24h), %
                        {sortBy === 'change' && (sortOrder === 'ascending' ? ' ↑' : ' ↓')}
                    </th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {sortedCryptocurrencies?.map((crypt: ICrypt) => (
                    <tr key={crypt.rank}>
                        <td>{crypt.symbol}</td>
                        <td>
                            <img height={32} width={32}
                                 src={`https://assets.coincap.io/assets/icons/${crypt.symbol.toLowerCase()}@2x.png`}
                                 alt={crypt.symbol}
                                 onError={handleImageError}
                            />
                        </td>
                        <td>{formatValue(crypt.priceUsd)}</td>
                        <td>{formatValue(crypt.marketCapUsd)}</td>
                        <td>{formatValue(crypt.changePercent24Hr)}</td>
                        <td>
                            <button>Add</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div>
                <button disabled={page === 1} onClick={() => setPage(prev => prev - 1)}>Previous</button>
                <span>{page}</span>
                <button onClick={() => setPage(prev => prev + 1)} disabled={!hasMoreData}>Next</button>
            </div>
        </div>
    );
};

export default CryptList;
