import React, { useState } from 'react';
import { ICrypt } from "../models/ICrypt";
import { cryptAPI } from "../services/CryptService";

const ITEMS_PER_PAGE = 15;

const CryptList: React.FC = () => {
    const [search, setSearch] = useState('');

    const {
        data: cryptocurrencies,
        error,
        isLoading,
        isFetchingNextPage,
        fetchNextPage,
        hasNextPage
    } = cryptAPI.useGetCryptocurrenciesQuery(
        { limit: ITEMS_PER_PAGE, offset: 0, search: search },
        {
            getNextPageParam: (lastPage, allPages) => {
                // Проверка, есть ли следующая страница (на основе вашего API)
                if (lastPage.data && lastPage.data.length === ITEMS_PER_PAGE) {
                    return allPages.length * ITEMS_PER_PAGE;
                }
                return false;
            }
        }
    );

    const formatValue = (value: string) => {
        const roundedToTwo = parseFloat(value).toFixed(2);
        if (roundedToTwo === "0.00") {
            return parseFloat(value).toFixed(5);
        }
        return roundedToTwo;
    };

    const handleImageError = (e: React.SyntheticEvent<HTMLImageElement, Event>) => {
        e.currentTarget.src = "https://coincap.io/static/logo_mark.png";
    }

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error fetching data</div>;

    return (
        <div>
            <input
                type="text"
                placeholder="Search..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
            />

            <table>
                <thead>
                <tr>
                    <th>Symbol</th>
                    <th>Logo</th>
                    <th>Price (USD)</th>
                    <th>Market Cap (USD)</th>
                    <th>Change (24h)</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {cryptocurrencies?.data?.map((crypt: ICrypt) => (
                    <tr key={crypt.rank}>
                        <td>{crypt.symbol}</td>
                        <td>
                            <img height={32} width={32}
                                 src={`https://assets.coincap.io/assets/icons/${crypt.symbol.toLowerCase()}@2x.png`}
                                 alt={crypt.symbol}
                                 onError={handleImageError}
                            />
                        </td>
                        <td>{formatValue(crypt.priceUsd)}</td>
                        <td>{formatValue(crypt.marketCapUsd)}</td>
                        <td>{formatValue(crypt.changePercent24Hr)}%</td>
                        <td>
                            <button>Add</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div>
                <button onClick={() => fetchNextPage()} disabled={!hasNextPage || isFetchingNextPage}>
                    {isFetchingNextPage ? 'Loading more...' : 'Load More'}
                </button>
            </div>
        </div>
    );
};

export default CryptList;
