import React, {useState} from 'react';
import {ICrypt} from "../models/ICrypt";
import {cryptAPI} from "../services/CryptService";

const ITEMS_PER_PAGE = 15;

const CryptList: React.FC = () => {
    const [page, setPage] = useState(1);
    const {data: cryptocurrencies, error, isLoading} = cryptAPI.useGetCryptocurrenciesQuery({
        limit: ITEMS_PER_PAGE,
        offset: (page - 1) * ITEMS_PER_PAGE
    });

    const [sortKey, setSortKey] = useState<keyof ICrypt | null>(null);
    const [sortAscending, setSortAscending] = useState(true);

    const sortedCryptocurrencies = cryptocurrencies?.data?.slice().sort((a, b) => {
        if (!sortKey) return 0;

        if (a[sortKey] > b[sortKey]) return sortAscending ? 1 : -1;
        if (a[sortKey] < b[sortKey]) return sortAscending ? -1 : 1;

        return 0;
    });

    const handleSortColumn = (key: keyof ICrypt) => {
        if (sortKey === key) {
            setSortAscending(prev => !prev);
        } else {
            setSortKey(key);
            setSortAscending(true);
        }
    };

    const formatValue = (value: string) => {
        const roundedToTwo = parseFloat(value).toFixed(2);
        if (roundedToTwo === "0.00") {
            return parseFloat(value).toFixed(5);
        }
        return roundedToTwo;
    }

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error fetching data</div>;

    return (
        <div>
            <table>
                <thead>
                <tr>
                    <th>Symbol</th>
                    <th>Logo</th>
                    <th>Price (USD)</th>
                    <th>Market Cap (USD)</th>
                    <th>Change (24h)</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {cryptocurrencies?.data?.map((crypt: ICrypt) => (
                    <tr key={crypt.rank}>
                        <td>{crypt.symbol}</td>
                        <td>
                            <img height={32} width={32}
                                 src={`https://assets.coincap.io/assets/icons/${crypt.symbol.toLowerCase()}@2x.png`}
                                 alt={crypt.symbol}/>
                        </td>
                        <td>{formatValue(crypt.priceUsd)}</td>
                        <td>{formatValue(crypt.marketCapUsd)}</td>
                        <td>{formatValue(crypt.changePercent24Hr)}%</td>
                        <td>
                            <button>Add</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div>
                <button disabled={page === 1} onClick={() => setPage(prev => prev - 1)}>Previous</button>
                <span>{page}</span>
                <button onClick={() => setPage(prev => prev + 1)}>Next</button>
            </div>
        </div>
    );
};

export default CryptList;
