import React, { useState } from 'react';
import {ICrypt} from "../models/ICrypt";
import {cryptAPI} from "../services/CryptService";

const ITEMS_PER_PAGE = 20;

const CryptList: React.FC = () => {
    const [page, setPage] = useState(1);
    const { data: cryptocurrencies, error, isLoading } = cryptAPI.useGetCryptocurrenciesQuery({ limit: ITEMS_PER_PAGE, offset: (page - 1) * ITEMS_PER_PAGE });

    const nonZeroCryptocurrencies = cryptocurrencies?.data?.filter(crypt => {
        return parseFloat(crypt.priceUsd) !== 0 && parseFloat(crypt.marketCapUsd) !== 0;
    });

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error fetching data</div>;

    return (
        <div>
            <table>
                <thead>
                <tr>
                    <th>Symbol</th>
                    <th>Logo</th>
                    <th>Price (USD)</th>
                    <th>Market Cap (USD)</th>
                    <th>Change (24h)</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {nonZeroCryptocurrencies?.map((crypt: ICrypt) => (
                    <tr key={crypt.rank}>
                        <td>{crypt.symbol}</td>
                        <td>
                            <img height={32} width={32} src={`https://assets.coincap.io/assets/icons/${crypt.symbol.toLowerCase()}@2x.png`} alt={crypt.symbol}/>
                        </td>
                        <td>{parseFloat(crypt.priceUsd).toFixed(2)}</td>
                        <td>{parseFloat(crypt.marketCapUsd).toFixed(2)}</td>
                        <td>{parseFloat(crypt.changePercent24Hr).toFixed(2)}</td>
                        <td>
                            <button>Add</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div>
                <button disabled={page === 1} onClick={() => setPage(prev => prev - 1)}>Previous</button>
                <span>{page}</span>
                <button onClick={() => setPage(prev => prev + 1)}>Next</button>
            </div>
        </div>
    );
};

export default CryptList;
