import React from 'react';
import {ICrypt} from "../models/ICrypt";
import {cryptAPI} from "../services/CryptService";

const CryptList: React.FC = () => {
    const { data: cryptocurrencies, error, isLoading } = cryptAPI.useGetCryptocurrenciesQuery(10);

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error fetching data</div>;

    return (
        <div>
            {cryptocurrencies?.data?.map((crypt: ICrypt) => (
                <div key={crypt.rank}>
                    <img height={64} width={64} src={`https://assets.coincap.io/assets/icons/${crypt.symbol.toLowerCase()}@2x.png`} alt={'logo'}/>{crypt.name}: {crypt.priceUsd}
                </div>
            ))}
        </div>
    );
};

export default CryptList;
